con:configuration.scriptText:
//check for Endpoint
log.info("EndPoint: "+messageExchange.getEndpoint()+" ms");
//check response time
assert(messageExchange.timeTaken < 4000);
//check for TimeTaken
log.info("Time Reponse: "+ messageExchange.getTimeTaken()+" ms");  

//check for header
log.info("Reponse Header Length: "+messageExchange.responseHeaders["Content-Length"])
assert(messageExchange.responseHeaders["Content-Length"] != null)

//check attachments
log.info(messageExchange.responseAttachments.length);
assert(messageExchange.responseAttachments.length == 0);

//validate response nodes
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
def requsetHolder = groovyUtils.getXmlHolder(messageExchange.requestContent);
def responseHolder = groovyUtils.getXmlHolder(messageExchange.responseContent);
def refNodeISOCode = responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sISOCode");
def refNodeName = responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sName");
assert(refNodeISOCode == "CHF" && refNodeName == "Swiss Francs");

//to get response
def resp = messageExchange.responseContentAsXml.toString()

//get json response
import groovy.json.JsonSlurper
def responseMessage = messageExchange.response.responseContent;
def json = new JsonSlurper().parseText(responseMessage);

//assert node values
def expectedName = "[Switzerland]";
def expectedCapital = "[Bern]";
log.info(json.name);
log.info(json.capital);
assert json.name.toString() == expectedName &&  (json.capital.toString() != null && json.capital.toString() == expectedCapital)

testStepName = messageExchange.modelItem.testStep.name//to get the Test Step Name
log.info testStepName
xmlHold = messageExchange.responseContentAsXml.toString()  //to store the response as Xml string

